void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 rgb = vec3(0.,0.,0.);
	float t= iTime;

	float f = iResolution.x/iResolution.y;
	float s =.25;
	vec2 pos  = mod(vec2(uv.x*f, uv.y)+vec2(t*0.15,t*0.1 ), vec2(s*.5)) - vec2(s*.25);
	vec2 pos2 = mod(vec2(uv.x*f, uv.y)+vec2(t*0.15,t*0.1), vec2(s)) - vec2(s*.5-s*.25);
	float d1 = dot(pos, pos);
	float d2 = dot(pos2, pos2);

	rgb.r+=(1.-step(iResolution.x*.0001,d1*100.))*uv.x*.6 *(uv.y*0.6+.4);
	rgb.g+=(1.-step(iResolution.x*.0003,d2*100.))*uv.y*.4;
	rgb.b=(rgb.r+rgb.g)*.5;
	rgb+= vec3((uv.x+uv.y)*.2, 0.05,0.1);



	float v1=step(uv.y*100.-10.,sin(uv.x*10.+t*.5)*10.+15.0)*.25;
	float v2=step(uv.y*120.-8., sin(uv.x*10.+t*1.5)*6.+12.0)*.25;
	float v3=step(uv.y*150.-6., sin(uv.x*10.+t*3.)*4.+10.0)*.25;

	rgb+=vec3(0., vec2(v1+v2+v3));

	fragColor = vec4(rgb,1.0);
}
