void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aspect = iResolution.x / iResolution.y;
	vec2 uv = -fragCoord.xy / iResolution.xy;	
    vec2 uva = vec2(uv.x, uv.y / aspect);

    float speed = 0.1;
    float summ = 0.0;
    for (int i = 0; i < int(12); ++i) {
    	vec2 uvt = uva;
    	float deltaX = mod(speed * iTime * sin(0.34 * float(i) + 1.0), 2.0);
		deltaX = abs(deltaX - 1.0);
        uvt.x += deltaX;

		float deltaY = mod(speed * iTime * sin(0.2 * float(i) + 1.0), 2.0 / aspect);
        deltaY = abs(deltaY - 1.0 / aspect);
		uvt.y += deltaY;

		float circle = 1. / max(.04, sqrt(dot(uvt, uvt)));
		summ = summ + circle;
	}  
	summ = smoothstep(0.6, 0.7, sin(summ));

	vec3 color = vec3(summ, summ, summ);
	fragColor = vec4(color, 1.0);
}
