#define ZITERS 64

float evalmag(vec2 c)
{
    float re = 0.0;
    float im = 0.0;
    float a = 0.0;
    float b = 0.0;

    for (int i = 0; i < ZITERS; i++ ) {
		a = re;
        b = im;
        re = a*a-b*b + c.x;
        im = a*b+b*a + c.y;
    }

    float mag = re*re + im*im;
    return mag;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    float t = iTime;
    float scale = 2.0 / min(res.x, res.y) / pow(t,3.0);
    vec2 pixelcenter = res * 0.5;
    vec2 cCenter = vec2(-0.745, 0.15);
    vec2 c0 = (fragCoord - res*0.5) * scale + cCenter;

    fragColor = vec4(0.0);
    float denom = 0.0;
    for(float dx = 0.0; dx <= 1.0; dx += 0.33 ) {
        for(float dy = 0.0; dy <= 1.0; dy += 0.33 ) {
            vec2 c = c0 + vec2(dx, dy);
   			float mag = evalmag(c0);
		    fragColor += mix( vec4(0.0), vec4(1.0), mag*0.5) / 9.0;
        }
    }    
}
