ivec2 hash(ivec2 i){
    ivec2 c = i*ivec2(961748927,314606869)+ivec2(3947586,435789);
    for(int h = 0; h < 2; h++){
        i = ivec2(i.x*i.x-i.y*i.y,2*i.x*i.y)+c;
    }
    return i;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 rnd = hash(ivec2(fragCoord));
    rnd = hash(rnd*(iFrame+1));

    fragColor.xyz = fract(vec3(rnd,hash(rnd).x)/float(0xfffffff));
}
