void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	//COLORS FOR SQUARES
    vec4 black = texture(iChannel1,uv);
    vec4 white = texture(iChannel0,uv);

    uv.y *= iResolution.y/iResolution.x;

    //SIZE OF BOARD
    float xSide = 20.0;
    float ySide = 20.0;

    //Black Layer
    fragColor = black;

    //Place white boxes on top of black background
    if (mod(uv.x * xSide, 2.0)> 1.0 && mod(uv.y * ySide, 2.0)>1.0 || 
        mod(uv.x * xSide, 2.0)< 1.0 && mod(uv.y * ySide, 2.0)<1.0){
     fragColor = white;   
    }
}
