 vec2 housePos = vec2(0.5f, 0.0f);
 vec2 chimenyPos = vec2(0.5f, 0.4f);
 vec2 windowPos = vec2(0.35, 0.2);
vec2 windowPos2 = vec2(0.55, 0.2);
 float houseLength = 0.4;
 float chimenyLength = 0.1;
 float windowLength = 0.1;
 vec3 color1 = vec3(0.7, 0.3,0.3);
 vec3 color2 = vec3(0.0, 1.0, 1.0);
 vec3 color3 = vec3(0.75, 0.5, 0.25);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;



    float aspect = iResolution.y /iResolution.x;
    uv.y *= aspect;
    housePos *= aspect;

    vec2 hv1 = step(housePos, uv) * step(uv, housePos + houseLength);
    vec2 hv2 = step(chimenyPos, uv) * step(uv, chimenyPos + chimenyLength);
    vec2 hv3 = step(windowPos, uv) * step(uv, windowPos + windowLength);
    vec2 hv4 = step(windowPos2, uv) * step(uv, windowPos2 + windowLength);


	vec4 Layer1 = vec4(1.0, 1.0, 1.0, 1.0);   
    vec4 Layer2 = vec4(color3, hv2.x * hv2.y);
    vec4 Layer3 = vec4(color2, hv3.x * hv3.y);
    vec4 Layer4 = vec4(color1, hv1.x * hv1.y);
    vec4 Layer5 = vec4(color2, hv4.x * hv4.y);


	vec4 Composite = mix(Layer1, Layer4, Layer4.a);
    Composite = mix(Composite, Layer2, Layer2.a);
    Composite = mix(Composite, Layer5, Layer5.a);
	Composite = mix(Composite, Layer3, Layer3.a);

    fragColor = Composite;
}
