float avg(vec3 v) { return (v.x + v.y + v.z) / 3.0; }

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float src = avg(texture(iChannel0, fragCoord/iResolution.xy).rgb);
    float src_sx = avg(texture(iChannel0, (fragCoord + vec2(1.0, 0.0))/iResolution.xy).rgb);
    float src_sy = avg(texture(iChannel0, (fragCoord + vec2(0.0, 1.0))/iResolution.xy).rgb);
    vec2 edges = vec2(src - src_sx, src - src_sy);
    float mag = length(edges);

    fragColor = vec4(vec3(mag / src), 1.0);
}
