//Object Locations
vec2 bodyLocation = vec2(0.5,0.4);
vec2 chimneyLoaction = vec2(0.8, 0.83);
vec2 windowLocation1 = vec2(0.35,0.45);
vec2 windowLocation2 = vec2(0.65,0.45);

//Objects dimensions 
float widthBody = 0.35;
float heightBody = 0.2;
float widthChim = 0.02;
float heightChim = 0.05;
float windowSize = 0.07;

//Object Colors
vec4 BG = vec4(1.0, 1.0,1.0,1.0);
vec4 houseColor = vec4(1.0, 0.0,0.0, 1.0);
vec4 chimneyColor = vec4 (0.0,0.0,0.0,1.0); 
vec4 windowColor = vec4 (1.0,1.0,0.0,1.0);

//A '1' means the layer is on and a '0' means the layer is off
float WindowLayerIsOn = 1.0;
float BodyLayerIsOn = 1.0;
float ChimneyLayerIsOn = 1.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //Fixing the aspect Ratio
    float aspect =iResolution.y/iResolution.x;

    //Applying the Aspect Raion to the objects
    uv.y *= aspect;
    bodyLocation.y *= aspect;
    chimneyLoaction.y *= aspect;
    windowLocation1.y *= aspect;
    windowLocation2.y *= aspect;


    //Creating the layers
    vec4 Layer1 = houseColor;
    vec4 Layer2 = chimneyColor;
    vec4 Layer3 = windowColor;
	vec4 Layer4 = windowColor;
    vec4 Layer5 = BG;

    //Creating the body
    Layer1 *= step(length(uv.x - bodyLocation.x), widthBody) * step(length(uv.y - bodyLocation.y), heightBody);
    //Creating the Chimney 
    Layer2 *= step(length(uv.x - chimneyLoaction.x), widthChim) * step(length(uv.y - chimneyLoaction.y), heightChim);

    //Creating the window in layer 3 and another in layer four
    Layer3 *= step(length(uv.x - windowLocation1.x), windowSize) * step(length(uv.y - windowLocation1.y), windowSize);
   	Layer4 *= step(length(uv.x - windowLocation2.x), windowSize) * step(length(uv.y - windowLocation2.y), windowSize);
    //Mix the two window layers into one
    Layer3 = mix(Layer3, Layer4, Layer4.a);

    //On and off switches
    Layer1 *=  BodyLayerIsOn;
    Layer2 *= ChimneyLayerIsOn;
    Layer3 *= WindowLayerIsOn;

    //Mixing all of the layers
    vec4 Composite = mix(Layer5, Layer1, Layer1.a);
    Composite = mix(Composite, Layer2, Layer2.a);
     Composite = mix(Composite, Layer3, Layer3.a);
    fragColor = Composite;




}
