// maze from https://www.shadertoy.com/view/4ldGDM
// variant of https://www.shadertoy.com/view/Xl33zl

#define T(u,v) texture( iChannel0, (U+vec2(u,v))/R )

void mainImage( out vec4 O,  vec2 U )
{  
    vec2 R = iResolution.xy, V;

    if ( iFrame<120 || iMouse.z>0. ) {
        V = (U-R/2.) * mat2(1,-1./1.73, 0,2./1.73) /8.+.1;          // conversion to
        float r = sin(1e5*length (ceil(V)));
        O += min(1.,.1 / fract( r >.5  ? V.x : r< -.5 ? V.y : V.x+V.y )) -O;  // maze
        //O += min(1.,.1 / fract( r >.5  ? V.x : r< -.5 ? V.y : 1.-V.x-V.y )) -O;  // maze
        //length(fract(1.-V)) < 1./8. ? O+=2. : O;                    // close holes at nodes

        U = fract(1e4*sin(U*mat2(1,17,23,-1.3)+iTime))*3e2; r = U.x;
        U.y > 1. ? r :
        //r = (1.-fract(1e5*sin(U.x+.121*U.y+iTime)))*1e3;    // (not precise enough)
        r<1. ? O.r++ : r<2. ? O.g++ : r<3. ? O.b++ : r;             // seeds in R, G, B
        return;
}

    O = T(0,0);   
    vec4 M = T(-1,0)+T(1,0)+T(0,-1)+T(0,1);                         // neighborhood
    O.a<.5 ? O+=step(.5,M-M.a) : O;                                 // reached by R/G/B floods
}
