void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / (vec2(sin(iTime) - cos(iTime), tan(iTime))) - iResolution.x / fragCoord.x;
    vec2 find = uv - normalize(fragCoord);
	fragColor = vec4((find.x/uv.x)*(find.y/uv.y)) - vec4(fragCoord / iResolution.xy, 1.0, 1.0) +
        vec4(faceforward(uv/iResolution.xy, fragCoord, find), 1.0, 1.0);
}
