#define OJOCOORD vec2(-0.025, 0.15)
const float PI = 3.14159265359;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cuv = fragCoord.xy / iResolution.xx * 10.0;
	vec2 uv = fract(cuv);
    cuv -= uv;

    uv -= 0.5;

    float s2 = sin(iTime + (cuv.x + cuv.y));
    float c2 = cos(iTime + (cuv.x + cuv.y));    
    float ts = (sin(iTime * 7.0) * 0.25) + 0.75;

    float rq = dot(uv, uv); //uv.x * uv.x + uv.y * uv.y;
    float smCircle = 1.0 - smoothstep(0.05 * ts, 0.051 * ts, rq);


    mat2 m = mat2(c2, s2, s2, -c2);
//    vec2 ruv = vec2(uv.x * c2 + uv.y * s2, uv.x * s2 - uv.y * c2);
//    vec2 rojo = vec2(OJOCOORD.x * c2 * ts + OJOCOORD.y * s2 * ts, OJOCOORD.x * s2 * ts - OJOCOORD.y * c2 * ts);
    vec2 ruv = m * uv;
    vec2 rojo = m * OJOCOORD * ts;

    vec2 ojo = uv - rojo;
    rq = dot(ojo, ojo); //ojo.x * ojo.x + ojo.y * ojo.y;
    float smEye = smoothstep(0.001 * ts, 0.0011 * ts, rq);    

    float angle = abs(atan(ruv.y, ruv.x + 0.06) / PI);    
    float s = abs(sin(iTime * 8.0) * 0.15);

    float smMouth = smoothstep(s, s + 0.01, angle);

    vec4 baseColor = mix( vec4(s2 + c2, s2 - c2, s2 * c2, 1.0), vec4(s2 - c2, s2 * c2, s2 + c2, 1.0), smCircle * smMouth * smEye);     

	fragColor = baseColor;
}
