#define R iResolution.xy 
#define ss smoothstep

void mainImage( out vec4 O, in vec2 U )
{
	U = (2.*U-R)/R.y;

    float a = 31.5,
    	  s = 1./a,	
          i = floor(U.x/s),
          it = floor(U.x/(1./(a/2.)));

    vec3 w = vec3(ss(0.5,0.9,U.y),
                  1.0-ss(0.5,0.9,abs(U.y)),
                  ss(0.5,0.9,-1.*U.y));

	float bw = mod(abs(it),2.);
    vec3 cy = vec3(1.00,0.83,0.42);
    vec3 cr = vec3(0.49,0.08,0.09);


    vec3 co = cr * w.y + cy * (bw > 0. ? w.x:w.z);


    O.xyz = mix(co,vec3(bw),abs(mod(i,2.)));
}
