//---------------------------------------------------------
//
// SoundView.image.glsl           by tholzer     2016-12-03
//
// Draw scrolling sound data with HSV colors.
// Set mouse.x to change scroll speed.
//
// Tags: sound, scroll, speed, view
//---------------------------------------------------------

// convert Hue-Saturation-Brightness to Red-Green-Blue color
vec3 hsv(float h, float s, float v)
{
  return mix(vec3(1.),
    clamp((abs(fract(h+vec3(3.,2.,1.)/3.)*6.-3.)-1.), 0., 1.),s)*v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c = texture(iChannel0, fragCoord.xy / iResolution.xy);
    vec3 x = hsv(c.r*1.5, c.g, c.b);
    fragColor = vec4(x,1.0);
}

//---------------------------------------------------------
//
// SoundView.BufA.glsl           by tholzer     
//
//   v1.0  2016-12-03 scroll from left to right  
//   v1.1  2018-10-11 scroll from right to left added 
//
// iChannel0:  https://soundcloud.com/uxmal-records/psyrius-uranus
// iChannel1:  BufferA
//
//---------------------------------------------------------

#define Right2Left 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    #ifdef Right2Left 
    if (fragCoord.x <= iResolution.x-2.0)
    {
      float scrollSpeed = 1.0 + iMouse.x*2.0 / iResolution.x;
      float pixelSize = scrollSpeed / iResolution.x;
      uv += vec2(pixelSize, 0.0);
      fragColor = texture(iChannel1, uv);
    }
    #else 
    if (fragCoord.x >= 1.0) 
    {
      float scrollSpeed = 1.0 + iMouse.x*2.0 / iResolution.x;
      float pixelSize = scrollSpeed / iResolution.x;
      uv -= vec2(pixelSize, 0.0);
      fragColor = texture(iChannel1, uv);
    }
    #endif
    else 
    {
      float v1 = texture(iChannel0, vec2(uv.y, 0.00)).r;
      float v2 = texture(iChannel0, vec2(uv.y, 0.25)).r;
      float v3 = texture(iChannel0, vec2(uv.y, 0.50)).r;
      float v4 = texture(iChannel0, vec2(uv.y, 0.75)).r;
      fragColor = vec4(v1,v2,v3,v4);  
    }
}
