void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos =-1.+2.*(fragCoord.xy/iResolution.y)-vec2(.8,-.1);
		//so this is my way of zoom the scene out, kind of breathing effect
    	 pos*=24.+cos((iTime)*2.)*atan(iTime*12.,3.);

    float newp= pow((pos.x),1.)*pow(pos.y,1.);

    //the noise
    newp+=float(vec2(24.1,24.1)//add some scale /pos*=24.  to get more dist
             *pow(cos(pos.y/pos.y)+sin(iTime*3.)*3.,4.)*
    		 	pow(cos((pos.x-1.)*pos.x-2.)+cos(iTime)*3.,4.)); 

    //noise on/off  the duration of the noise-fx tan returns -1. +1. (maybe)  
    (.2*tan(iDate.w))>.4?pos.x+=pos.x*newp*pos.y:newp/=1.;//newp/0. will cls

	//the palm tree function - https://www.shadertoy.com/view/XssSRX
	float r = sin(pos.x+(iTime*.5))//+2.
            + abs(sin(.123 * 3.1416)) * 12.5 *.5 * cos( 
                atan(pos.y+tan(iTime),//more breathing rolling screen
                     pos.x-12.) 			//reposition the main shape
                		*12.612 );

     float col, light; vec2 rot=vec2(0.1,0.1); 

     for (float i=1.0; i<=155.0; i+=5.)
		{  
        rot += vec2(.3*cos(i/8.+sin(iTime*3.)*3.0/2.0),
                   		6.*sin(i/15.-cos(iTime)*-3.0/2.0)); 
         light = (.5+.5*tan(iDate.w))>.02?
         col = smoothstep(r*12.-pos.y,.1/newp,24./r-pos.y)://MASK

         //chain thing
    	 light +=.15/distance(pos+rot.yx,-pos/newp*(rot.x))+
            	  .15/length(pos-rot.y+cos(rot.xy*.54))+
        		   .15/length(pos+(rot.y)-rot.yx)
            		//three arms are ok for now
					//+.15/distance(pos+(rot.xy),pos/newp*(rot.x))
                       ; 
    	}
   	//main light - the big one
    light+=2.12/length(pos-1.4*rot.x+(rot.yx));

    //some outlines for the white areas of the shape
 	float eys =.05/smoothstep(distance(fract(r*0.1),.5/r)//distortion of the lines
       				*sin(iTime),.02/r,.5);

  	col+=col*eys-.8;//constrast 1. to .1

	fragColor= mix (vec4(vec3(col),1.)/.6,
                     vec4(1.-vec3(eys-.1,eys-.1,eys-.6),1.),.25)
        				+vec4(vec3(light-.2,light,light),1.); 	  
}
