void mainImage( out vec4 O, vec2 I)
{
    vec2 R= iResolution.xy,
    M=iMouse.x>0.?2.*iMouse.xy/R:vec2(1.6);
	I = (I+I -R)/R.x;
    O+=1e6-O;
    for(int i=0;i<20;i++){
        //you can modify the factor if you want to, it is interesting. You can also replace sin with an other periodic function
        I=.5*sin(M*I/dot(I,I)); // I/dot(I,I) <=> normalize(I)/length(I) ( length inversion)
        O=min(O,vec4(length(I),min(abs(I.x),abs(I.y)),abs(dot(I,vec2(1))),0)); //Orbit traps
         }
}
