void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//iTime is the float time in seconds, matches the counter next to the pause button below the preview window.
    //Since it will quickly get out of the 0..1 range, it's often desirable to map it back down somehow.

    //Periodic
    float fPeriodic = sin(iTime) * 0.5 + 0.5; //Sin is [-1..1], sin*0.5 + 0.5 is [0..1]
    float fRepeat = fract(iTime); //Fractional component of iTime, repeats 0..1 each second.

	fragColor = vec4(fPeriodic, fRepeat, 0.0, 1.0);
}
