// forked from https://www.shadertoy.com/view/Xsl3RX by possum
const float rings      = 23.0;
const float velocity   = 1.5;
const vec3  pumpv      = vec3( 0.5 );
const vec3  pumpoffset = vec3( 0.05, 0.0, 0.0);
const vec3  pumpamp    = vec3( 1.90, 2.1, 1.7);
const float p          = 0.5;
const float b          = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2  R      = iResolution.xy,
        pos    = ( fragCoord - 0.5 * R ) / R.y;

  float dist   = pow( length ( pos ), p );
  float offset = iTime * velocity;
  vec3  conv   = ( sin( iTime * pumpv ) + pumpoffset ) * pumpamp + rings;
  vec3  v      = dist * conv - offset;
  vec3  ringr  = floor( v );
  vec3  color  = smoothstep(
                   -b, b,
                   abs( dist - (
                     ringr.x + float( fract( v.x ) > 0.5 ) + offset
                   ) / conv ));
  fragColor    = vec4( mix( color, 1.0 - color, mod( ringr, 2.0 )), 1.0 );      
}
