// Based on "rotated rays background " by Andrey A. Ugolnik
// https://www.shadertoy.com/view/MlXGRX


const float raysCount = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;


    vec3 color0 = texture(iChannel0, uv).rgb;
	vec3 color1 = texture(iChannel1, uv).rgb;

	float smoothness = 0.5;
    //float smoothness = (1.0 + sin(iTime  2.0)) * 0.5;

	vec2 p = (-iResolution.xy + 2.0 * fragCoord.xy) / iResolution.y;

    // background: 弧度， 半径
	vec2 q = vec2(atan(p.y, p.x), length(p));

	//float f = smoothstep(-smoothness, smoothness, sin(q.x * raysCount + iTime));
    //float f = smoothstep(-smoothness, smoothness, sin(q.y * raysCount + iTime));
    float f = smoothstep(-smoothness, smoothness, sin(pow(q.y, 3.) + iTime));

    f *= smoothstep(-smoothness, smoothness, sin(q.x * raysCount + iTime));

	vec3 col = mix(color0, color1, 1.-f);
    fragColor = vec4(col, 1.0);
}
