// 2D gradient noise
// - Based on shader by iq
// - See https://www.shadertoy.com/view/XdXGW8

vec2 hash( vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

// Attempt at creating a 3D gradient noise by moving layers of noise with different
// frequencies around in circles
float noise3d(vec3 st)
{
    return noise(st.xy*20.0 + vec2(cos(st.z*0.07), sin(st.z*0.07)) * 20.0)
         * noise(st.xy*11.0 + vec2(cos(st.z*-0.05), sin(st.z*-0.05)) * 10.0)
         * noise(st.xy*7.0  + vec2(cos(st.z*0.1), sin(st.z*0.1)) * 9.0)
        ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;

    float col;
    vec2 pos = vec2(uv.x*32.0, uv.y*32.0);
    if(fragCoord.x < iResolution.x * 0.499)
    {
        col = noise(pos) * 2.0;
    }
        if(fragCoord.x > iResolution.x * 0.501)
    {
        col = noise3d(vec3(uv.x, uv.y, iTime)) * 50.0;
    }

     fragColor = vec4(col, col, col, 1.0);
}
