#define pi (acos(-1.))
vec3 col(float p)
{
    //Thermal gradient colors inspired by yx
    //https://www.shadertoy.com/view/XldyzN
    vec3 a = vec3(0, .5, 0);
    vec3 b = vec3(1., .5, .4);
    vec3 c = vec3(.3, .6, 1.2);
    vec3 d = vec3(-.25, -.6, -.25);
    vec3 e = vec3(0,0,.7);
    return a+b*cos(2.*pi*(c*p+d))+e*p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 colo = col(sqrt(1.+abs(texture(iChannel0, uv).r))-1.);

    // Output to screen
    fragColor = vec4(colo,1.0);
}

float samp(vec2 fragCoord)
{
    float v = texture(iChannel0, fragCoord/iResolution.xy).r;
    if(v>1000.)
        v=1000.;
    if(v<0.)
        v=0.;
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = vec2(200. + cos(iTime*0.5)*150. + 80.*sin(1.4132*iTime), 140. + sin(iTime*0.296)*320.)/vec2(400.,330.);
    vec2 pos2 = vec2(200. + cos(iTime*0.43 + 7.)*130. + 100.*sin(1.2132*iTime-10.), 150. + sin(iTime*0.196)*270.)/vec2(400.,330.);
    float s = texture(iChannel2, vec2(0.005, 0.)).r*0.5;
    pos = mod(pos+1., 1.);
    pos2 = mod(pos2+1., 1.);

    if(iMouse.z > 0.)
        pos = iMouse.xy/iResolution.xy;

    if(iFrame<=20)
    {
        fragColor = texture(iChannel1, uv)*0.5;
    }
    else if(length(pos-uv) <= 2./iResolution.y || length(pos2-uv) <= 2./iResolution.y)
        fragColor = vec4(10.*s,0.,0., 1.);
    else
    {
        float heat = samp(fragCoord);
        float diff = 0.;
        diff += heat - samp(fragCoord + vec2(1.,0.));
        diff += heat - samp(fragCoord + vec2(-1.,0.));
        diff += heat - samp(fragCoord + vec2(0.,1.));
        diff += heat - samp(fragCoord + vec2(0.,-1.));
        diff += (heat - samp(fragCoord + vec2(1.,1.))) / sqrt(2.);
        diff += (heat - samp(fragCoord + vec2(-1.,-1.))) / sqrt(2.);
        diff += (heat - samp(fragCoord + vec2(1.,-1.))) / sqrt(2.);
        diff += (heat - samp(fragCoord + vec2(-1.,1.))) / sqrt(2.);
        float c = -1.24 - 0.245*sin(iTime*0.2);
        if(s>0.)
            c = (2.8+0.3*cos(iTime))*-s;
        diff *= c / (4.+4./sqrt(2.));
        heat += texture(iChannel1, uv).r*(0.002*s);
    	fragColor = vec4((heat + diff)*0.99,0.0,1.0,1.0);
    }
}
