float plot(float a, float b) {
	return smoothstep(3.0 / iResolution.y, 0.0, abs(a - b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 bg = vec3(1.0);
    bg = mix(bg, vec3(1.0, 0.0, 0.0), plot(uv.y, 0.5 * sin(uv.x * 2.0 + iTime)));
    bg = mix(bg, vec3(0.0, 1.0, 0.0), plot(uv.y, 0.5 * sin(uv.x * 2.0 + iTime + 3.14159 * 0.33)));
    bg = mix(bg, vec3(0.0, 0.0, 1.0), plot(uv.y, 0.5 * sin(uv.x * 2.0 + iTime + 3.14159 * 0.66)));
	fragColor = vec4(bg, 1.0);
}
