vec2 bs( float s )
{
    if(sin(iTime) > s)
    {
        float x = 0.0;
        for(int i = 0; i < 10; i++)
        {
            x += smoothstep(s, 0.1, x + 1.0 * s);
            x -= s;
        }
        s += x / s;
    }
    return vec2(step(dot(s - s, s*s), abs(sin(mod(s - s, s * s)) / fract(s))));
}
void mainImage( out vec4 O, in vec2 U )
{
    O-=O;
	vec2 uv = U.xy / iResolution.xy;
	O = smoothstep(vec4(dot(uv,uv)), O*O+fract(vec4(U, uv+bs(float(U/uv)))), vec4(uv,cos(sin(step(uv,uv-uv)))));
}
