#define COLOR vec4(1.0,1.0,1.0,0.5)
#define SPEED 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 back = texture(iChannel0,uv); 

    vec4 light = COLOR;

    float offset;

    //offset = iMouse.x / iResolution.x;

    offset = cos(mod(iTime * SPEED,3.14 * 0.5));

    float a = uv.x + offset;

    a = a * offset;

    a = step(a,1.0) * a;

    a = max(a,0.0);

    a *= light.a; 

	fragColor = back * (1.0 - a) + light * a;

    //fragColor = vec4(offset,0.0,0.0,1.0);
}
