void mainImage( out vec4 O,  vec2 U )
{
    float y = 6.*U.y/iResolution.y, 
          x = 4.*U.x/iResolution.x,  n=0./0.,
          a = x<1.? 0./0. : x<2.? sqrt(-1.) : x<3.? 1./0. : -1./0.;

    if (fract(x)>.98   || fract(y/2.)>.98) { O = vec4(1,0,0,1); return; }
    if (fract(y/2.)<.5 && fract(y-.5)>.98) { O = vec4(0,1,0,1); return; }

    U -= .5;
    O =   y < .5  ? vec4( mod(n, U.x) )
        : y < 2.  ? vec4( mod(a, U.x) )
        : y < 2.5 ? vec4( n* sin(U.x) )
        : y < 4.  ? vec4( a* sin(U.x) )
        : y < 4.5 ? vec4( n*U.x )     // n*U.x -n
        :           vec4( a*U.x );    // a*U.x -a

    // bug art by 834144373
    //O =  vec4( mod(0./0.,U.x*U.y)); 
    //O =  vec4( mod(0./0.,U.x/U.y));

}
