float xOscs = 30.0;
float yOscs = 90.0;
float xPropSpeed = 5.0;
float yPropSpeed = 5.0;
float globalAmplitude = 3.480;
float globalOffset = 0.024;
float decay = 7.936;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;

    vec2 r = st - vec2(0.5);
    vec2 up = vec2(0.0, 1.0);
    float theta = acos(dot(r, up));
    float crossDot = dot(vec3(0.0,0.0,-1.0), cross(vec3(r.x,r.y,0.0), vec3(up.x,up.y,0.0)));
    theta *= crossDot;

    st.x = length(r) * cos(theta + iTime);
    st.y = length(r) * sin(theta + iTime);

    float x = globalOffset +  globalAmplitude * exp(-st.x/decay) * sin(st.x * xOscs - iTime *xPropSpeed);
    float y = globalOffset +  globalAmplitude * exp(-st.y/decay) * sin(st.y * yOscs - iTime *yPropSpeed);

    vec3 color = vec3(pow(x,0.520) + pow(y,0.520));    

	fragColor = vec4(color,1.0);
}
