vec3 backgroundColor = vec3(0.0, 0.0, 0.0);
float rotRadius = 40.0;
float speed = 8.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 100.0;
    vec2 screenCenter = (iResolution.xy / 2.0);
    vec2 circleCenter = screenCenter + vec2(sin(iTime * speed) * rotRadius, cos(iTime * speed) * rotRadius);
    vec3 result = backgroundColor;

    vec2 dir = fragCoord - circleCenter;
    float len = length(dir);
    if(len < radius){
        result = vec3(sin(len));
    }
    else if(length(fragCoord - screenCenter) < (radius + rotRadius)){
       	result = vec3(sin(fragCoord.y + iTime * -40.0));
    }
    else {
        result += vec3(sin(fragCoord.x / 1.0 + iTime * - 20.0) + sin(fragCoord.y / 1.0));
    }

	fragColor = vec4(result,1.0);
}
