void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 uv = fragCoord.xy / iResolution.xy;
    float uvt = uv.x + iTime * .6;
    float uvy = uv.y + .1 + sin(iTime*1.) * .1;
    float height = 1.- smoothstep(.15, .49, uvy) * .55* (cos(uvy*2.2 * (1.0 + .07 *cos(uvt*6.0 + 1.4)))  + .22 
                                                  + .017 *cos(uvt*89.38) 
                                                  + .03  *cos(uvt*46.38) 
                                                  + .012 *cos(uvt*19.7 + .12) 
                                                  + .03  *sin(uvt*5.7+7.5));

    height = smoothstep(.6,.75, height);

    vec2 suncenter = vec2(((uv.y-.8) * .4) * (uv.x - .5) +uv.x - .5, uvy + (sin(uv.x *85.*(uv.y+.8) + iTime *.8)*.01*(uv.y - 1.)) - .9);

    vec2 sun_repeat = vec2(0,tan(suncenter.y*4.) );
    float sun = 1. + 1.5 * smoothstep(.1, .2, length(mod(suncenter,sun_repeat) - sun_repeat  * .5));

    vec3 red = vec3(.99, .14, .34);
    vec3 blue = vec3(.26, .29, 1);
    vec3 col = mix(red,blue, height - abs(.26 - uvy + .1 * sin(uvt)));

    fragColor = vec4(col * height * (sun),1.0);
}
