vec4 wave(vec4 def, vec2 xy) {
    float start = def.x;
    float height = def.y + start / 25.0 * sin(iTime) + xy.y / 20.0 - 20.0;
    float width = def.z;
 	float offset = 10.0 * sin(float(iTime) / start); 

    float t = (
        start + height/2.0
        + sin(width * (xy.x * (1.0 - xy.y/3.0/iResolution.y) + xy.y/2.0) / 100.0  + offset)
        	* height / 2.0
        - xy.y);

    if (t > -20.0) {
        return vec4(0.3, 0.3, 0.6 + 0.29 * (t / -15.0), 1.0);
    } else {
        return vec4(0.0, 0.0, 0.0, 0.0);
    }
}

vec4 mixWithWave(vec4 cur, vec4 def, vec2 xy) {
	vec4 r = wave(def, xy);
    if (r.a == 1.0) return r;
    else return cur;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
//    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
  //  vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 col = vec4(0.0);
    vec2 xy = fragCoord;
    col = mixWithWave(col, vec4(220.0, 25.0, 2.1, 0.0), xy);
    col = mixWithWave(col, vec4(145.0, 35.0, 2.4, 0.0), xy);
    col = mixWithWave(col, vec4(70.0, 40.0, 2.7, 0.0), xy);
    col = mixWithWave(col, vec4(10.0, 45.0, 3.0, 0.0), xy);
    //col = mixWithWave(col, vec4(1.2, vec3(0.8)), xy);


    // Output to screen
    fragColor = vec4(col);
}
