void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float p; // Which point to check, based on noise
    float pc; // Proposed color amount
    float i;
    //////////////////////////////R/////
    float r = 1.; // Red Amount
    for (i; i < 100.; i++)
    {
        pc = length(uv - 2.5*vec2(texture(iChannel0,vec2(i*.003,iTime/1020.))));
        if (pc < r)
        {
            r = pc;
        }
    }
    //////////////////////////////G/////
    pc = 0.;
    i = 0.;
    float g = 1.; // Green Amount
    for (i; i < 100.; i++)
    {
        pc = length(uv - 2.5*vec2(texture(iChannel0,vec2(i*.002,iTime/1000.))));
        if (pc < g)
        {
            g = pc;
        }
    }
    pc = length(uv - (iMouse.xy/iResolution.y));
        if (pc < g)
        {
            g = pc;
        }
    //////////////////////////////B////
    pc = 0.;
    i = 0.;
    float b = 1.; // Blue Amount
    for (i; i < 100.; i++)
    {
        pc = length(uv - 2.5*vec2(texture(iChannel0,vec2(i*.004,iTime/1100.))));
        if (pc < b)
        {
            b = pc;
        }
    }
    /////////////////////////////////////
    vec3 c = (1. - 6.*vec3(r,g,b));


    fragColor = vec4(c,1.0);
}
