void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float ridus = 0.2;
    vec2 center = vec2(0.5, 0.66);

    vec4 col = vec4(1.0);
    float d = length(uv - center);
    if (d <= ridus) {
        float g = d / ridus;
        g = sin(g*3.14*0.5);
        g = g*g;
    	col = vec4(g);
    }

    vec4 c = texture(iChannel0, uv);
    col = c * (1.0 - col);

    fragColor = col;
}
