void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy/2.)/iResolution.y;
    vec3 ro = vec3(.0*cos(iTime), 1., -1.5);
    vec3 rd = normalize(vec3(uv*2., 1.));

    //rd = vec3(0,0,1);
    //ro.xy += uv*2.;

    vec4 accum = vec4(vec3(0,0,.5), 1) * 5e-6; // multiplier is fog distance
    for (int x=0 ; x<40 ; x++)
    for (int y=0 ; y<10 ; y++)
    {
        // xyz: position, w: radius
        vec4 point = texelFetch(iChannel0, ivec2(x, y), 0).xzyw;

        // xyz: color, w: sharpness
        vec4 color = texelFetch(iChannel0, ivec2(x, 10+y), 0);

        point.xyz -= ro;
        float d = length(point.xyz);
        float a = dot(point.xyz, rd); // distance to disc plane
        float r = length(rd*a - point.xyz) / point.w; // distance to disc center
        float alpha = 1. / (1. + pow(r, 50. * (color.w+0.0000000001)));
        //float alpha = 1. / (1. + pow(r, 50. * (color.w))); // WTF ?!? why is it black
        float w = exp(-point.z * 5.) * alpha;
        w /= point.z; // compensate higher density of points when further away
        accum += vec4(color.xyz, 1) * w;
        //accum = mix(accum, color, alpha*color.w);
    }

   	fragColor = accum/accum.w;

    if (less(fragCoord, vec2(100, 100))) // raw point view
    	fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

void world( out vec4 fragColor, in vec2 fragCoord )
{
    if (less(fragCoord, vec2(10., 20.))) // grass
    {
        if (fragCoord.y >= 10.)
        {
            fragColor = vec4(.5,.9,.2,1.);
            return;
        }
        vec2 uv = (fragCoord - vec2(0., 0.)) /vec2(10., 10.);
        fragColor = vec4(uv*2.-1., 0., .11);
        return;
    }

    if (less(fragCoord, vec2(20., 20.))) // trunk
    {
        if (fragCoord.y >= 10.)
        {
            fragColor = vec4(.4,.1,.0,1.);
            return;
        }
        vec2 uv = (fragCoord - vec2(10., 0.)) / vec2(10., 10.);
        float a = PI * 2. * uv.x;
        float r = 0.1;
        fragColor = vec4(cos(a)*r, sin(a)*r, uv.y, .15);
        return;
    }

    if (less(fragCoord, vec2(30., 20.))) // treetop
    {
        if (fragCoord.y >= 10.)
        {
            fragColor = vec4(.2,.5,.1,1.);
            return;
        }
        vec2 uv = (fragCoord - vec2(20., 0.)) / vec2(10., 10.);
        float a = PI * 2. * uv.x;
        float b = PI * (uv.y - .5);
        uv = vec2(cos(a), sin(a))*.3;
        fragColor = vec4(uv*cos(b)+.0, sin(b)*.5+.9, .2);
        return;
    }

    const vec3 hpos = vec3(.9, -.5, 0);
    const float hrad = .5;
    const float hlen = .7;
    if (less(fragCoord, vec2(34., 20.))) // pillars
    {
        vec2 uv = (fragCoord - vec2(30., 0.)) / vec2(1., 10.);
        if (fragCoord.y >= 10.)
        {
            fragColor = mix(vec4(1), vec4(1,0,0,1), sin(uv.y*12.)*.5+.5);
            return;
        }
        fragColor = vec4(
            hpos + vec3(0, uv.x*hrad-.5, uv.y * hlen),
            .1
        );
        return;
    }

    if (less(fragCoord, vec2(37., 20.))) // hoops
    {
        vec2 uv = (fragCoord - vec2(34., 0.)) / vec2(1., 10. / PI);
        if (fragCoord.y >= 10.)
        {
            fragColor = mix(vec4(1), vec4(1,0,0,1), sin(uv.y*12.)*.5+.5);
            return;
        }
        fragColor = vec4(
            hpos + vec3(0, (uv.x + cos(uv.y)*.5 - .5)*hrad, hlen + sin(uv.y)*hrad),
            .1
        );
        return;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    world(fragColor, fragCoord);

    if (fragCoord.y < 10.)
    {
        float a = iTime*.2;
        float c = cos(a), s = sin(a);
        mat2 R = mat2(c, s, -s, c);
        fragColor.xy *= R;
    }
}

#define PI 3.141592653589793
bool less(vec2 a, vec2 b)
{
    return a.x<b.x && a.y<b.y;
}
