#define PI 3.141593

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    float px = 2.0/iResolution.y;
    float ar = iResolution.x / iResolution.y ;
    //uv = uv - vec2(0.5*ar,0.5) ;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec2 mm = vec2(iMouse.xy/iResolution.xy);
    //mm.x *= ar ;
    //mm = vec2(0.0,-0.0) ;
    float fact = 1.0+0.5*sin(iTime*2.0*PI/3.0) ;
    //uv *= fact ;

    //Backgound
    float sd = 0.1 ;
    float t = iTime ;
    vec3 col = vec3(0.2,0,0) ;
    col.r = 0.5*sign((mod(uv.x+0.3*sin(0.4*t),sd)-0.5*sd)*(mod(uv.y+0.3*sin(0.4*t),sd)-0.5*sd)) ;



    // Balls
    vec2 b1 = vec2(0.5,0.0)+(0.6,0.6)*sin(0.3*iTime+iTime) ;
    vec2 b2 = vec2(-0.5,0.0)+(-0.3,0.6)*sin(0.6*iTime) ;
    vec2 b3 = vec2(0.5,0.2)+(-0.6,-0.8)*sin(0.6*iTime+0.2) ;
    float d1 = length(b1-uv) ; 
    col = mix(col, vec3(1.0,0.0,0.0), 1.0-smoothstep(0.10,0.10+px,d1)) ;
    float d2 = length(b2-uv) ; 
    col = mix(col, vec3(0.0,1.0,0.0), 1.0-smoothstep(0.10,0.10+px,d2)) ;
    float d3 = length(b3-uv) ; 
    col = mix(col, vec3(0.0,0.0,1.0), 1.0-smoothstep(0.10,0.10+px,d3)) ;
    col = mix(col, vec3(1.0,1.0,0.0),1.0-smoothstep(0.01,0.01+3.0*px,abs((d1+d2)-1.6)));
    col = mix(col, vec3(0.0,1.0,1.0),1.0-smoothstep(0.01,0.01+3.0*px,abs((d2+d3)-1.6)));
    col = mix(col, vec3(1.0,0.0,1.0),1.0-smoothstep(0.01,0.01+3.0*px,abs((d1+d3)-1.6)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
