void mainImage( out vec4 f, in vec2 w ) {
    vec4 p = vec4(w,0.,1.)/iResolution.x-.55, d=p, t, c;
    float T = iTime, C = cos(T), S = sin(T);
    p.z += T;
    for(float i = 2.; i >0.; i-=.01)
    {
        t = mod(p, 8.)-4.;
        c = texture(iChannel0, mat3(C,0,S, 0,1,0, -S,0,C) * t.xyz);
        t = length(t.xyz)-3. + c;
        if(t.x<.01) break;
        p -= d*t.x*.2;
        f = c*i+i*.2;
    }
}
