bool cirOL(vec2 center, float rad)
{
    return(length(center) < rad);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 col1 = vec4( 0., 0.4, 1., 1.);
    vec4 col2 = vec4( 0., 0.2, 1., 1.);
    vec4 col3 = vec4( 0., 0.,  1., 1.);
    vec4 col4 = vec4( 0., 0.6, 1., 1.);
    vec4 col5 = vec4( 0., 0.8, 1., 1.);
    vec4 col6 = vec4( 0., 0.10, 1., 1.);
    vec4 col7 = vec4( 0., 0.1, 1., 1.);

    float f = abs(sin(iTime * 0.02));
    int counter = 0;

    vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    float c2 = 0.;
    for(float i = -2.; i < 2.; i += 0.05)
    { 	
        c2+= 10.;
        vec2 temp = r;
        temp.x += i;
        temp.y += sin(c2 * (iTime /500.));
        if(cirOL(temp, f)){ counter += 1; }
    }


    if(counter == 0)
    {
        fragColor = vec4(0., 0., 0., 1.);
    }

    else if(counter % 2 == 0)
    {   
        fragColor = col3;
    }

    else if(counter % 3 == 0)
    {
		fragColor = col1;
    }

    else if(counter % 7 == 0)
    {
        fragColor = col4;
    }
    else if(counter % 11 == 0)
    {
        fragColor = col5;
    }

    else if(counter % 13 == 0)
    {
        fragColor = col5;
    }
    else if(counter % 17 == 0)
    {
        fragColor = col5;
    }

    else
    {
        fragColor = col2;
    }


}
