void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    uv = mod(uv*0.8,fract(iTime*4.028));
    //uv = uv*2.0-1.0;

    float time = iTime;

    float t = texture(iChannel0, uv).r;


    vec2 gr = vec2(sin(time+uv.x*0.3),cos(time+uv.y*0.6));
    float r = texture(iChannel0, gr*2.97).r;

    vec2 tx = vec2(1.0/iResolution.x,1.0/iResolution.y);

    float vv = fract(sin(time*tan(time)));

    float b = 0.0;
    float c = 0.0;
    for ( int i=0; i<32; i++ )
    {
    	b += texture(iChannel0,vec2(uv.x*vv,uv.y-r)/(tx.xy*float(i)*(cos(time*r)*0.5+0.5)*3.2)).r;
        c += texture(iChannel0,vec2(uv.x-r,uv.y*vv)/(tx.xy*float(i)*(sin(time*r)*0.5+0.5)*7.0)).r;

    }
    b /= 32.0;
    c /= 32.0;



	float e = smoothstep(-vv,vv+0.4,b-c);
    fragColor = vec4(e,e,e,1.0);

}

vec2 mainSound( in int samp,float time)
{
    vec2 bb = vec2( sin(0.420*440.0*time)*exp(sin(-6.0*time*0.2)) ); 
    vec2 tt = vec2( cos(0.420*440.0*time)*0.5+0.5 )*exp(sin(-4.0*time*7.2)) ; 
    return mix((bb+tt),mod(tt,bb*0.1),sin(bb));
}
