vec3 hsl2rgb(in float h, float s, float l) {
  vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
  return l + s * (rgb-0.5)*(1.0-abs(2.0*l-1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float t = iTime + 1000000.0;
  vec2 uv = fragCoord.xy / iResolution.xy;
  float x = uv.x, y = uv.y;
  x *= 4.0; y *= 4.0;
  float h = sin(sin(t*400.0+sin(x*x+y*y+t)*t) + sin(t*3.0+x*x+y*sin(x*y))) * x;
  float s = 1.0 * mod(h, 1.0);
  float l = 0.5 * mod(s, 1.0) * mod(h, 1.0);
  fragColor = vec4(hsl2rgb(h, s, l), 1.0);
}
