renderpass.code:
vec4 RED = vec4(1.0, 0.0, 0.0, 1.0);
vec4 GREEN = vec4(0.0, 1.0, 0.0, 1.0);
vec4 BLUE = vec4(0.0, 0.0, 1.0, 1.0);
vec4 WHITE = vec4(1.0, 1.0, 1.0, 1.0);
vec4 BLACK = vec4(0.0, 0.0, 0.0, 1.0);
vec4 ALPHA = vec4(0.0, 0.0, 0.0, 0.0);

vec2 boxPoint1 = vec2(0.2, 0.75);
vec2 boxPoint2 = vec2(0.8, 0.25);

vec2 chimneyPoint1 = vec2(0.25, 0.85);
vec2 chimneyPoint2 = vec2(0.3, 0.75);

vec2 window1Point1 = vec2(0.3, 0.65);
vec2 window1Point2 = vec2(0.4, 0.35);

vec2 window2Point1 = vec2(0.6, 0.65);
vec2 window2Point2 = vec2(0.7, 0.35);

//change to 0.0 to remove layer
float layer1On = 1.0;
float layer2On = 1.0;
float layer3On = 1.0;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 background, layer1, layer2, layer3;

    background = WHITE;

    layer1 = RED;
    layer1.a = step(boxPoint1.x, uv.x) * step(1.0 - boxPoint2.x, 1.0 - uv.x) * step(boxPoint2.y, uv.y) * step(1.0 - boxPoint1.y, 1.0 - uv.y);
    layer1.a *= layer1On;

    layer2 = GREEN;
    layer2.a = step(chimneyPoint1.x, uv.x) * step(1.0 - chimneyPoint2.x, 1.0 - uv.x) * step(chimneyPoint2.y, uv.y) * step(1.0 - chimneyPoint1.y, 1.0 - uv.y);
    layer2.a *= layer2On;

    layer3 = BLUE;
    layer3.a = step(window1Point1.x, uv.x) * step(1.0 - window1Point2.x, 1.0 - uv.x) * step(window1Point2.y, uv.y) * step(1.0 - window1Point1.y, 1.0 - uv.y);
    layer3.a += step(window2Point1.x, uv.x) * step(1.0 - window2Point2.x, 1.0 - uv.x) * step(window2Point2.y, uv.y) * step(1.0 - window2Point1.y, 1.0 - uv.y);
    layer3.a *= layer3On;

    vec4 composite = mix(background, layer1, layer1.a);
    composite = mix(composite, layer2, layer2.a);
    composite = mix(composite, layer3, layer3.a);

    fragColor = composite;


}
