void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv=uv*9.;

    vec3 color  = vec3(0.3);
//    uv.x= uv.x + step(1.,mod(uv.y,2.))*cos(iTime);
    if(mod(uv.y, 2.)>=1.)
       uv.x=uv.x+0.5; //sin(iTime);
    else
       uv.x=uv.x;//-cos(iTime);
//    uv.x= uv.x + step(1.,mod(uv.y,2.))*sin(iTime);

    uv = fract(uv);


    vec2 border1=step(vec2(0.05), uv);
    vec2 border2 = 1.-step(vec2(0.9),uv);


    float k=border1.x*border1.y*border2.x*border2.y;

    color = vec3(k*0.8);

    fragColor = vec4(color,1.0);
}
