void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //checkerboard assignment #1

    float numSquaresX = 7.0;
    float numSquaresY = 4.0;

	vec2 uv = fragCoord.xy / iResolution.xy; 
    vec4 text1 = texture(iChannel0, uv);
    vec4 text2 = texture(iChannel1, uv);
    vec4 tempColor = text1;
    float seeThrough = 0.0; // 0.0 - 1.0 higher = less transparent.


    //set uv to be the number of squares and use floor to convert it to a pseudo int
	uv.x = floor(uv.x*numSquaresX);
    uv.y = floor(uv.y*numSquaresY);

    //every square with even numbered coordinates is set to text2
    if (mod(uv.x, 2.0) == mod(uv.y, 2.0)){
		tempColor = (tempColor*seeThrough + text2) / 1.1; // divide by 1.1 to change brightness.
    }
	fragColor = tempColor;
}
