void mainImage( out vec4 fragColor, in vec2 fragCoord )

#define r iResolution.xy
#define t iTime
{
    vec2 uv = fragCoord/r;
    vec2 d = vec2 (.3*sin(t),.1*cos(t));
    uv-=.5;
    uv.x*=r.x/r.y;
    float l = length(uv+d)*4.5*(2.0*uv.y+1.0);
    l=l;
    fragColor = vec4(0.25/l,0.4/l,0.5/l,1.0); 
}
