void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy / iResolution.xy;
	vec2 p = (-1.0+2.0*q)*vec2(iResolution.x/iResolution.y,1.0);   
    vec2 uv = p;
    float d = length(p)*10.0+0.5;
    float phi = atan(p.y/p.x)+ iTime*0.5;
	float dp = (2.0+1.0*tan(phi*6.0));
	d = d + dp;
    uv=vec2(p)/d+iMouse.xy/iResolution.xy;
	vec3 col = texture(iChannel0, uv).rgb;
    float r = pow( pow(p.x*p.x,7.0) + pow(p.y*p.y,4.0), 1.0/6.0 );
	col = col*r;
    fragColor = vec4(col, 1.0);
}
