void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float d = distance(iResolution.xy*0.5,fragCoord.xy)*0.006;

    vec4 black = vec4(0.0, 0.0, 0.0, 1.0);

    vec4 color0 = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 color1 = mix(color0, black, 0.7);
    vec4 color2 = mix(color0, black, 0.8);
    vec4 color3 = mix(color0, black, 0.9);
    vec4 color4 = mix(color0, black, 1.0);
    vec4 color5 = black;

    float step0 = 0.0;
    float step1 = 0.2;
    float step2 = 0.4;
    float step3 = 0.6;
    float step4 = 0.8;
    float step5 = 1.0;

    vec4 color = mix(color0, color1, smoothstep(step0, step1, d));
	color = mix(color, color2, smoothstep(step1, step2, d));
	color = mix(color, color3, smoothstep(step2, step3, d));
	color = mix(color, color4, smoothstep(step3, step4, d));
	color = mix(color, color5, smoothstep(step4, step5, d));

    fragColor = color;

}
