#define tau 6.28318530717958647692

#define ChangeFactor 40.

#define rot(a) mat2(C=cos(a),S=sin(a),-S,C)


highp float rand(vec2 co) {
    return fract(sin(mod(dot(co.xy ,vec2(12.9898,78.233)),tau/2.))*43758.5453);
}

float obj(vec2 uv,float x) {
	float S,C;
    float r = rand(round(uv/tau+0.8));

    vec2 gain = round(uv/tau)*tau+1.5;
    uv-=gain;
    uv*=rot(tau/pow(2.,ceil(r*2.)));
    uv+=gain;

	float f = sin(uv.x)+sin(uv.y);
	float b = sin(uv.x+uv.y);
    x+=r*ChangeFactor;

	float col = f-b/(tan(x)/sin(x));
	return smoothstep(0., 1., col*col*col*col*col);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
	fragColor = vec4(obj(fragCoord.xy/10.,iTime*10.));
}
