#define VIEW_RAW_BUFFER 0

const vec3 color1 = vec3(0.7, 0.6, 0.4);
const vec3 color2 = vec3(0.1, 0.2, 0.4);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
#if VIEW_RAW_BUFFER
    fragColor = texture(iChannel0, uv);
#else
    float val = step(0.1, length(texture(iChannel0, uv).rg));
	fragColor = vec4(mix(color1, color2, val), 1.0);
#endif
}

vec2 to_uv(in vec2 in_pixels) {
    return in_pixels / iResolution.xy;
	// return 0.1 + mod(vec2(0.8) + in_pixels / iResolution.xy, vec2(0.9));
}

vec2 fetch_dir(in vec2 in_pixels) {
    vec2 dir = texture(iChannel0, to_uv(in_pixels)).rg;
    dir = step(vec2(0.5), dir) * vec2(step(dir.y, dir.x), step(dir.x, dir.y));
    return dir;
}

void mainImage( out vec4 fragColor, in vec2 p )
{
    const float refresh_period = 15.0;
    float t = iTime - mod(iTime, refresh_period);
    mat2 rotator = mat2(cos(t), sin(t),
                          -sin(t), cos(t));
    vec2 dir = step(vec2(0.98), texture(iChannel1, to_uv(rotator * p)).rg);
    dir = vec2(step(dir.y, dir.x), step(dir.x, dir.y)) * dir;

    if (mod(iTime, refresh_period) < 0.1) {
      fragColor = vec4(dir, 0.0, 1.0);
        return;
    }

    if (dot(dir, dir) < 0.5) {
	    dir = step(vec2(0.9), texture(iChannel0, to_uv(p)).rg);
    }

    if (dot(dir, dir) < 0.01) {
    	vec2 dir1 = vec2(1.0, 0.0);
    	vec2 dir2 = dir1.yx;


    	float val1 = max(abs(dot(dir1, fetch_dir(p + dir1))),
                     	abs(dot(dir1, fetch_dir(p - dir1))));


    	float val2 = max(abs(dot(dir2, fetch_dir(p + dir2))),
        	             abs(dot(dir2, fetch_dir(p - dir2))));


        if (val1 > val2) {
            val2 = 0.0;
        } else {
            val1 = 0.0;
        }

        val1 = step(0.9, val1);
        val2 = step(0.9, val2);


    	dir = mix(dir, dir1, val1);
    	dir = mix(dir, dir2, val2);
    }

    dir = step(vec2(0.9), dir) * vec2(step(dir.y, dir.x), step(dir.x, dir.y));

    fragColor = vec4(dir,0.0,1.0);
}
