void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float inc = uv.x / 100.0 + uv.y / 100.0;    
    vec3 col00 = texture( iChannel0, uv ).xyz;
    vec3 colm0 = texture( iChannel0, vec2(uv.x - 0.01 / iResolution.x, uv.y) ).xyz;
    vec3 result = col00 + colm0 + inc;
    result = fract(result);
    fragColor = vec4(result, 1.0);
}
