void mainImage( out vec4 O, vec2 U )
{
    float y = U.y;
    U -=  4.*iTime;
    if (fract(iTime/6.)<.5) U /= 2.1;  // at zoom = 2, we are just on Nyquist limit.
	O = y>iResolution.y/3.
        ? texture( iChannel0, U   / iChannelResolution[0].xy )
        : texture( iChannel0, U.x / iChannelResolution[0].xy ).rrrr;
}
