mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.st/iResolution.st;

    vec4 tex = texture(iChannel2, st);

    st -= vec2(0.5);
    st *= rotate2d((tex.r-0.5)*2.0*0.00024);

    float scl = 1.0-length(st)*0.01 - 0.0024;
    st *= mat2(scl, 0.0, 0.0, scl);
    st += vec2(0.5);

    vec2 texel = 1.0/iResolution.xy;

    vec2 offset[9];

    offset[0] = vec2(-texel.x, -texel.y);
    offset[1] = vec2(     0.0, -texel.y);
    offset[2] = vec2( texel.x, -texel.y);
    offset[3] = vec2(-texel.x,      0.0);
    offset[4] = vec2(     0.0,      0.0);
    offset[5] = vec2( texel.x,      0.0);
    offset[6] = vec2(-texel.x,  texel.y);
    offset[7] = vec2(     0.0,  texel.y);
    offset[8] = vec2( texel.x,  texel.y);

    float kernel[9];

    kernel[0] = 0.0625; kernel[1] = 0.1250; kernel[2] = 0.0625;
    kernel[3] = 0.1250; kernel[4] = 0.2500; kernel[5] = 0.1250;
    kernel[6] = 0.0625; kernel[7] = 0.1250; kernel[8] = 0.0625;

    vec4 sum = vec4(0.0);

    for (int i = 0; i < 9; i++){
        sum += texture(iChannel2, st + offset[i]) * kernel[i];
    }

    vec4 color = vec4(0.0);

    if(iFrame <= 10 || iMouse.z > 0.0){
        color = texture(iChannel0, st);
    } else {
        color = vec4(sum.rgb, 1.0);
    }

    fragColor = color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.st/iResolution.st;
    vec2 texel = 1.0/iResolution.xy;

    vec2 offset[9];

    offset[0] = vec2(-texel.x, -texel.y);
    offset[1] = vec2(     0.0, -texel.y);
    offset[2] = vec2( texel.x, -texel.y);
    offset[3] = vec2(-texel.x,      0.0);
    offset[4] = vec2(     0.0,      0.0);
    offset[5] = vec2( texel.x,      0.0);
    offset[6] = vec2(-texel.x,  texel.y);
    offset[7] = vec2(     0.0,  texel.y);
    offset[8] = vec2( texel.x,  texel.y);

    float kernel[9];

    kernel[0] =   0.0; kernel[1] = -0.5; kernel[2] =   0.0;
    kernel[3] = -0.5; kernel[4] =   3.0; kernel[5] = -0.5;
    kernel[6] =   0.0; kernel[7] = -0.5; kernel[8] =   0.0;

    vec4 sum = vec4(0.0);

    for (int i = 0; i < 9; i++){
        sum += texture(iChannel0, st + offset[i]) * kernel[i];
    }

    vec4 color = vec4(clamp(sum.rgb, 0.0, 1.0), 1.0);

    fragColor = color;
}
