// Concentric Sine Waves
// Simon Gladman | http://flexmonkey.blogspot.co.uk | September 2017

float frequency = 10.0;
float amplitude = 0.1; 
float ringWidth = 0.1; 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color0 = vec3(abs(cos(iTime * 0.7)), 
                       abs(cos(iTime * 0.5)), 
                       0.0); 

	vec3 color1 = vec3(0.0, 
                       abs(cos(iTime)), 
                       abs(cos(iTime * 0.3))); 

	float amplitude = 0.1 * cos(iTime);

	vec2 position = (fragCoord.xy / iResolution.xy) - 0.5;

    float angle = atan(position.x, position.y) * frequency + iTime; 

    float distance = (mod(length(position) + (sin(angle) * amplitude), ringWidth)) / ringWidth;

    vec3 color = mix(color0, color1, distance);

    fragColor = vec4(color ,1.0);
}
