void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec2 p = uv;
  p.y = 0.;
  p.x = uv.y;
  vec4 t1 = texture( iChannel0,p);
  p.y = 1.0;
  vec4 t0 = texture( iChannel0,p);
  p.x /= 4.;
  vec4 t2 = texture( iChannel0,p);
  //uv.x = (uv.x-0.5) *(1.- t1.y*0.25) +0.5;
  uv.y += (t0.y-0.5)*0.25;
  uv.x += (t2.y-0.5)*0.5;
  fragColor = texture(iChannel1,uv*1.);
}
