vec4 fnoise( vec2 pos ) {
 vec4 back = vec4( 0.0, 0.0, 0.0, 0.0 );
 float scale = 1.0;
    for ( int i = 0; i != 5; ++i ) {
        scale *= 2.0;
        back += texture(iChannel0, scale * pos ) / scale;
    }
    return back;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 lightPos = vec3( 20.0 * iMouse.xy / iResolution.xy ,  5.0 );

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 rval = fnoise( (uv + 0.01 * vec2( iTime , iTime )) / 20.0 );
    float zval = rval.w;
    vec3 pos = vec3( uv, zval );
    vec3 l = normalize( lightPos - pos );
    vec3 n = normalize( rval.xyz );
    float dterm = clamp( dot( l, n ), 0.0, 1.0 );

    vec3 cloud = mix( 
        vec3( 0.8, 0.7, 0.4 ),
        vec3( 1.0, 1.0, 1.0 ),
        dterm
     );



	fragColor = mix(
        vec4( cloud, 1.0 ),
        vec4( 0.0, 0.0, 1.0, 1.0 ),
      	zval
    );
  //  fragColor = vec4(n, 1.0);
}
